	comp_seq
	, user_seq
	, module_seq
	, module_name
	, sn_no
	, title
	, content
	, del_yn
	, create_seq
	, modify_seq
)	VALUES (
	#{compSeq}
	, #{userSeq}
	, #{moduleSeq}
	, #{moduleName}
	, #{snNo}
	, #{title}
	, ''
	, 'N'
	, #{userSeq}
	, #{userSeq}
)

mapper.update:
/* updatePostContent */
UPDATE t_post
SET
	content	= #{content}
WHERE	post_seq	= #{postSeq}
